        {
            if (!(unit is TemperatureUnit unitAsTemperatureUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(TemperatureUnit)} is supported.", nameof(unit));

            return As(unitAsTemperatureUnit);
        }

        /// <summary>
